* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html, body {
  width: 100%;
  height: 100%;
  font-family: "Montserrat", sans-serif;
  color: #E6E6E6;
  background-color: #141414;
  overflow-x: hidden;
  display: flex;
  justify-content: center;
}

.BodyWrap {
  width: 100%;
  height: 100%;
  position: relative;
}

h1 {
  font-family: aktiv-grotesk-condensed, sans-serif;
  font-weight: 900;
  font-style: normal;
  text-transform: uppercase;
}

ul {
  list-style-type: none;
}

a {
  text-decoration: none;
  color: #E6E6E6;
}

.GroundZero {
  width: 100%;
  height: 100%;
}

/* --- Global ------------ */
/* --- Navigation ------------ */
.Nav {
  width: 100%;
  height: 100px;
  position: fixed;
  top: 0;
  left: 0;
  padding: 0px 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10000;
  transition: padding 0.5s ease-in-out;
}

.NavList {
  display: flex;
  justify-content: space-between;
  width: 180px;
  transition: width 0.5s ease-in-out;
}

.NavLink {
  color: #E6E6E6;
  font-size: 18px;
  font-weight: 200;
  transition: font-size 0.5s ease-in-out;
}

@media screen and (max-width: 650px) {
  .Nav {
    padding: 0px 35px;
  }

  .NavList {
    width: 160px;
  }

  .NavLink {
    font-size: 16px;
  }
}
/* --- Portfolio Page ------------- */
.Portfolio {
  width: 100%;
  height: 100%;
  position: relative;
}

.WindowWrap {
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10;
}

.Window {
  width: 65vw;
  height: 70vh;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  border-radius: 2px;
  box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.311);
  transition: height 0.5s ease-in-out;
}

@media screen and (max-width: 650px) {
  .Window {
    height: 50vh;
  }
}
.PortfolioContent {
  width: 100%;
  position: absolute;
  padding: 0 10%;
  z-index: 20;
}

.PortfolioInner {
  width: 100%;
  height: 100%;
}

/* -- Title ----- */
.PortfolioTitle {
  margin-top: 100px;
  font-size: 130px;
  display: inline-block;
}

@media screen and (max-width: 1260px) {
  .PortfolioTitle {
    font-size: 90px;
  }
}
@media screen and (max-width: 500px) {
  .PortfolioTitle {
    font-size: 50px;
  }
}
/* -- Context ----- */
.PortfolioContext {
  width: 75vw;
  max-width: 1400px;
  margin-top: 70px;
  display: flex;
  justify-content: space-between;
}

.PortfolioContext-Title {
  white-space: nowrap;
  width: 500px;
  font-size: 17px;
}

.PortfolioContext-Text {
  width: 800px;
  font-size: 17px;
  line-height: 24px;
}

@media screen and (max-width: 500px) {
  .PortfolioContext {
    flex-direction: column;
  }

  .PortfolioContext-Title {
    width: 100%;
    margin-bottom: 20px;
    font-size: 14px;
  }

  .PortfolioContext-Text {
    width: 100%;
    font-size: 14px;
  }
}
/* -- Project Links ----- */
.PortfolioProjects {
  margin-top: 300px;
  transition: margin-top 0.5s ease-in-out;
}

.Project {
  margin-bottom: 50px;
  position: relative;
}

.ProjectNumber {
  position: absolute;
  font-size: 13px;
  font-weight: 400;
  margin-top: 15px;
  transition: margin-top 0.5s ease-in-out;
}

.ProjectLink {
  font-size: 150px;
  font-family: aktiv-grotesk-condensed, sans-serif;
  font-weight: 900;
  font-style: normal;
  text-transform: uppercase;
  outline: none;
}

.ProjectLink:hover {
  color: #debdc7;
}

.ProjectDescription {
  font-size: 13px;
  text-transform: uppercase;
  margin-top: -10px;
  margin-left: 5px;
}

@media screen and (max-width: 1732px) {
  .PortfolioProjects {
    margin-top: 200px;
  }
}
@media screen and (max-width: 1261px) {
  .PortfolioProjects {
    margin-top: 300px;
  }
}
@media screen and (max-width: 840px) {
  .ProjectLink {
    font-size: 17vw;
  }
}
@media screen and (max-width: 696px) {
  .PortfolioProjects {
    margin-top: 140px;
  }
}
@media screen and (max-width: 650px) {
  .ProjectNumber {
    font-size: 2vw;
    margin-top: 0;
  }

  .ProjectDescription {
    font-size: 2vw;
  }
}
@media screen and (max-height: 800px) {
  .PortfolioProjects {
    margin-top: 130px;
  }
}
/* -- Connect ----- */
.PortfolioConnect {
  margin-top: 140px;
  margin-bottom: 280px;
}

.PortfolioConnect-TextBox {
  width: 600px;
  display: flex;
  justify-content: space-between;
}

.PortfolioConnect-Email {
  font-size: 14px;
  font-weight: 200;
}

.PortfolioConnect-SocialWrap {
  width: 300px;
  display: flex;
  justify-content: space-between;
}

.SocialTag {
  font-size: 14px;
  font-weight: 200;
}

@media screen and (max-width: 780px) {
  .PortfolioConnect-TextBox {
    flex-direction: column;
  }

  .PortfolioConnect-Email {
    margin-bottom: 15px;
  }

  .PortfolioConnect-SocialWrap {
    width: 230px;
  }
}
/* -- End Marker ----- */
.PortfolioEndMarker {
  width: 100%;
  height: 22px;
  position: absolute;
  bottom: 0;
  left: 0;
  background-color: #add1d7;
  display: flex;
  justify-content: center;
  align-items: center;
}

.EndMarkerHook {
  color: #141414;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
}

/* --- About Page ------------- */
.About {
  width: 100%;
  height: 100%;
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: cadetblue;
}

