  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  width: 100%;
  height: 100vh;
  font-family: "Montserrat", sans-serif;
  color: #E6E6E6;
  background-color: #141414;
  overflow-x: hidden;
  display: flex;
  justify-content: center;
}

.BodyWrap {
  width: 100%;
  height: 100%;
  position: relative;
}

h1 {
  font-family: aktiv-grotesk-condensed, sans-serif;
  font-weight: 900;
  font-style: normal;
  text-transform: uppercase;
}

ul {
  list-style-type: none;
}

a {
  text-decoration: none;
  color: #E6E6E6;
}

.GroundZero {
  width: 100%;
  height: 100%;
}

/* --- Global ------------ */
/* --- Navigation ------------ */
.Nav {
  width: 100%;
  height: 100px;
  position: fixed;
  top: 0;
  left: 0;
  padding: 0px 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10000;
}

.NavList {
  display: flex;
  justify-content: space-between;
  width: 180px;
}

.NavLink {
  color: #E6E6E6;
  font-size: 18px;
  font-weight: 200;
}

/* --- Portfolio Page ------------- */
.Portfolio {
  width: 100%;
  height: 100%;
  position: relative;
}

.WindowWrap {
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10;
}

.Window {
  width: 65vw;
  height: 70vh;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  box-shadow: 3px 3px 8px rgba(0, 0, 0, 0.297);
}

.PortfolioContent {
  width: 100%;
  position: absolute;
  padding-left: 10%;
  z-index: 20;
}

.PortfolioInner {
  width: 100%;
  height: 100%;
}

/* -- Title ----- */
.PortfolioTitle {
  margin-top: 100px;
  font-size: 90px;
  display: inline-block;
}

/* -- Context ----- */
.PortfolioContext {
  width: 75vw;
  margin-top: 50px;
  display: flex;
  justify-content: space-between;
}

.PortfolioContext-Title {
  white-space: nowrap;
  width: 500px;
  font-size: 17px;
}

.PortfolioContext-Text {
  width: 800px;
  font-size: 17px;
  line-height: 24px;
}

/* -- Project Links ----- */
.PortfolioProjects {
  margin-top: 150px;
}

.Project {
  margin-bottom: 50px;
  position: relative;
}

.ProjectNumber {
  position: absolute;
  font-size: 13px;
  font-weight: 400;
}

.ProjectLink {
  font-size: 150px;
  font-family: aktiv-grotesk-condensed, sans-serif;
  font-weight: 900;
  font-style: normal;
  text-transform: uppercase;
}

.ProjectLink:hover {
  color: #debdc7;
}

.ProjectDescription {
  font-size: 13px;
  text-transform: uppercase;
  margin-top: -10px;
  margin-left: 5px;
}

@media screen and (max-width: 840px) {
  .ProjectLink {
    font-size: 17vw;
  }
}
@media screen and (max-width: 670px) {
  .ProjectNumber {
    font-size: 2vw;
  }

  .ProjectDescription {
    font-size: 2vw;
  }
}
/* -- End Marker ----- */
.PortfolioEndMarker {
  width: 100%;
  height: 22px;
  position: absolute;
  bottom: 0;
  left: 0;
  background-color: #add1d7;
  display: flex;
  justify-content: center;
  align-items: center;
}

.EndMarkerHook {
  color: #141414;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
}

/* --- About Page ------------- */
.About {
  width: 100%;
  height: 100%;
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: cadetblue;
}

